import React, { Component } from "react";
import FriendCard from "./components/FriendCard";
import Wrapper from "./components/Wrapper";
import Title from "./components/Title";
import friends from "./friends.json";

class App extends Component {
  // Setting this.state.friends to the friends json array
  state = {
    friends,
    score:0,
    topscore:0
  };
  // remove friend will add 1 to the score and randomize the order of the pictures
  // removeFriend = id => {
  //   // takes the friends array and sorts the pictures into a random order
  //   friends.sort(() => Math.random() - .5);

  //   this.setState({ friends, score: this.state.score +1 });
  //   this.setState({ friends, clicked: true });
  // };

  removeFriend = id => {
    // Filter this.state.friends for friends with an id not equal to the id being removed
    // const friends = this.state.friends.filter(friend => friend.id !== id);
    friends.sort(() => Math.random() - 0.5 );
    // this.state.score++;
    if (!this.state.clicked){
    // Set this.state.friends equal to the new friends array
    this.setState({ friends, score: this.state.score +1 });
    this.setState({friends, clicked: true })
  }
  else {friends.forEach(friends => friends.setState({clicked:false}));
  this.setState({friends, score:0});
 }
 }

  render() {
    return (
      <Wrapper>
        <Title>Friends List Score: { this.state.score}</Title>
        {this.state.friends.map(friend => (
          <FriendCard
            removeFriend={this.removeFriend}
            id={friend.id}
            key={friend.id}
            name={friend.name}
            image={friend.image}
            occupation={friend.occupation}
            location={friend.location}
          />
        ))}
      </Wrapper>
    );
  }
}

export default App;
